import { Injectable, signal, WritableSignal } from "@angular/core";


export type UiScreenSizes = '' | 'lg' | 'md' | 'sm';

export interface UiClassSettings {
  active?: boolean;
  screenSizes?: UiScreenSizes[];
  value?: string;
}

@Injectable({
  providedIn: 'root'
})
export class UiClassService<
  N extends keyof T,
  T extends { [K in N]: UiClassSettings } = {[K in N]: UiClassSettings}
> {

  public get class(): string {
    return this.combine(
      ...this.names.map(name => this.composeName(name))
    );
  }

  public get names(): (keyof T)[] {
    return Object.keys(this.#class()) as (keyof T)[];
  }

  #class: WritableSignal<T> = signal<T>({} as unknown as T);

  public add(
    className: keyof T,
    value: string = '',
    screenSizes: UiScreenSizes | UiScreenSizes[] = '',
    active: boolean = true
  ): void {
    this.#class.update(current => current = { ...current, [className]: {
      active,
      screenSizes: Array.isArray(screenSizes) ? screenSizes : [screenSizes],
      value
    }} as T);
  }

  public update(
    className: keyof T,
    value: string = '',
    screenSizes: UiScreenSizes | UiScreenSizes[] = '',
    active: boolean = true
  ): void {
    this.#class.update(current => {
      const existing = current[className] || {};
      return { ...current, [className]: {
        active,
        screenSizes: Array.isArray(screenSizes) ? screenSizes : [screenSizes],
        value
      }} as T;
    });
  }

  public combine(...classes: (string | false | null | undefined)[]): string {
    return classes.filter((cls): cls is string => !!cls).join(' ');
  }

  public composeName(className: keyof T): string {
    const classNameArr: string[] = [];
    this.#class()[className]['screenSizes']?.forEach(size => {
      if (size.length >= 0 && this.#class()[className]['active']) {
        const value = this.#class()[className]['value'];
        classNameArr.push(`
          ${size.length > 0 ? `${size}:` : ''}` +
          `${className as string}` +
          `${value && value.length > 0 ? `-${value}` : ''}
        `);
      }
    });
    return classNameArr.join(' ');
  }

  public remove(className: keyof T): void {
    this.#class.update(current => {
      const { [className]: _, ...rest } = current;
      return rest as T;
    });
  }

  public toggle(className: keyof T): void {
    this.#class.update(current => {
      const isActive = current[className];
      return { ...current, [className]: !isActive } as T;
    });
  }
}

import { UiPaddingX } from "./ui-padding-x.interface";
import { UiPaddingY } from "./ui-padding-y.interface";
import { UiPaddingLeft } from "./ui-padding-left.interface";
import { UiPaddingRight } from "./ui-padding-right.interface";
import { UiPaddingTop } from "./ui-padding-top.interface";
import { UiPaddingBottom } from "./ui-padding-bottom.interface";

export interface UiPadding<
  PX = string,
  PY = string,
  PL = string,
  PR = string,
  PT = string,
  PB = string,
> extends UiPaddingX<PX>, UiPaddingY<PY>, UiPaddingLeft<PL>, UiPaddingRight<PR>, UiPaddingTop<PT>, UiPaddingBottom<PB> {}

export interface UiPaddingBottom<PB = string> {
  pb?: PB;
}

export type SpacingType = 'padding' | 'margin';

export type UiSpacing<
  Type extends SpacingType,
  X = string,
  Y = string,
  Left = string,
  Right = string,
  Top = string,
  Bottom = string,
> = {
  [K in Type extends 'padding' ? `p${'x' | 'y' | 'l' | 'r' | 't' | 'b'}` : `m${'x' | 'y' | 'l' | 'r' | 't' | 'b'}`]?: 
    K extends `${any}x` ? X :
    K extends `${any}y` ? Y :
    K extends `${any}l` ? Left :
    K extends `${any}r` ? Right :
    K extends `${any}t` ? Top :
    K extends `${any}b` ? Bottom :
    never;
};

export interface UiPaddingBottom<PB = string> extends Pick<UiSpacing<'padding', string, string, string, string, string, PB>, 'pb'> {}


const a: UiPaddingBottom = {
  pb: '4',
};

export interface UiPaddingLeft<PL = string> {
  pl?: PL;
}

export interface UiPaddingRight<PR = string> {
  pr?: PR;
}

import { UiPaddingBottom } from "./ui-padding-bottom.interface";
import { UiPaddingLeft } from "./ui-padding-left.interface";
import { UiPaddingRight } from "./ui-padding-right.interface";
import { UiPaddingTop } from "./ui-padding-top.interface";
import { UiPaddingX } from "./ui-padding-x.interface";
import { UiPaddingY } from "./ui-padding-y.interface";
import { UiPadding } from "./ui-padding.interface";

export interface UiPaddingSettings<Padding extends UiPadding> extends
  UiPaddingX<Padding['px']>,
  UiPaddingY<Padding['py']>,
  UiPaddingLeft<Padding['pl']>,
  UiPaddingRight<Padding['pr']>,
  UiPaddingTop<Padding['pt']>,
  UiPaddingBottom<Padding['pb']> {}

export interface UiPaddingTop<PT = string> {
  pt?: PT;
}

export interface UiPaddingX<
  PX = string,
> {
  px?: PX;
}

export interface UiPaddingY<PY = string> {
  py?: PY;
}

