import { Injectable, signal, WritableSignal } from "@angular/core";


export type UiScreenSizes = '' | 'lg' | 'md' | 'sm';

export interface UiClassSettings {
  active?: boolean;
  screenSizes?: UiScreenSizes[];
  value?: string;
}

@Injectable({
  providedIn: 'root'
})
export class UiClassService<
  N extends keyof T,
  T extends { [K in N]: UiClassSettings } = {[K in N]: UiClassSettings}
> {

  public get class(): string {
    return this.combine(
      ...this.names.map(name => this.composeName(name))
    );
  }

  public get names(): (keyof T)[] {
    return Object.keys(this.#class()) as (keyof T)[];
  }

  #class: WritableSignal<T> = signal<T>({} as unknown as T);

  public add(
    className: keyof T,
    value: string = '',
    screenSizes: UiScreenSizes | UiScreenSizes[] = '',
    active: boolean = true
  ): void {
    this.#class.update(current => current = { ...current, [className]: {
      active,
      screenSizes: Array.isArray(screenSizes) ? screenSizes : [screenSizes],
      value
    }} as T);
  }

  public update(
    className: keyof T,
    value: string = '',
    screenSizes: UiScreenSizes | UiScreenSizes[] = '',
    active: boolean = true
  ): void {
    this.#class.update(current => {
      const existing = current[className] || {};
      return { ...current, [className]: {
        active,
        screenSizes: Array.isArray(screenSizes) ? screenSizes : [screenSizes],
        value
      }} as T;
    });
  }

  public combine(...classes: (string | false | null | undefined)[]): string {
    return classes.filter((cls): cls is string => !!cls).join(' ');
  }

  public composeName(className: keyof T): string {
    const classNameArr: string[] = [];
    this.#class()[className]['screenSizes']?.forEach(size => {
      if (size.length >= 0 && this.#class()[className]['active']) {
        const value = this.#class()[className]['value'];
        classNameArr.push(`
          ${size.length > 0 ? `${size}:` : ''}` +
          `${className as string}` +
          `${value && value.length > 0 ? `-${value}` : ''}
        `);
      }
    });
    return classNameArr.join(' ');
  }

  public remove(className: keyof T): void {
    this.#class.update(current => {
      const { [className]: _, ...rest } = current;
      return rest as T;
    });
  }

  public toggle(className: keyof T): void {
    this.#class.update(current => {
      const isActive = current[className];
      return { ...current, [className]: !isActive } as T;
    });
  }
}

import { Component, HostBinding, inject, Injectable, Input, signal, WritableSignal } from '@angular/core';
import { UiPaddingService } from './ui-padding.service';  // Import the service

@Injectable({
  providedIn: 'root'
})
export abstract class UiPaddingClass {
  // protected paddingService = inject<UiPaddingService>(UiPaddingService);  // Inject the service

  @HostBinding('class')
  get hostClass(): string {
    const paddingClasses = `${this.paddingService.px} ${this.paddingService.py} ${this.paddingService.pl} ${this.paddingService.pr} ${this.paddingService.pt} ${this.paddingService.pb}`;
    const additional = this.getAdditionalClasses();
    return `${paddingClasses} ${additional}`.trim();
  }

  constructor(protected paddingService: UiPaddingService) {}


  @Input() set px(value: number) {
    this.paddingService.set({ px: String(value) });
  }
  @Input() set py(value: number) {
    this.paddingService.set({ py: String(value) });
  }
  @Input() set pl(value: number) {
    this.paddingService.set({ pl: String(value) });
  }
  @Input() set pr(value: number) {
    this.paddingService.set({ pr: String(value) });
  }
  @Input() set pt(value: number) {
    this.paddingService.set({ pt: String(value) });
  }
  @Input() set pb(value: number) {
    this.paddingService.set({ pb: String(value) });
  }

  protected getAdditionalClasses(): string {
    return '';
  }
}

import { Injectable, signal, WritableSignal } from '@angular/core';
// Interfaces
import { UiPadding, UiPaddingSettings } from './interface';

@Injectable({
  providedIn: 'root'
})
export class UiPaddingService<
  Padding extends UiPadding = UiPadding,
  SelectedPadding extends UiPaddingSettings<Padding> = UiPaddingSettings<Padding>
> {
  public get px(): SelectedPadding['px'] {
    return this.#px();
  }

  public get py(): SelectedPadding['py'] {
    return this.#py();
  }

  public get pl(): SelectedPadding['pl'] {
    return this.#pl();
  }

  public get pr(): SelectedPadding['pr'] {
    return this.#pr();
  }

  public get pt(): SelectedPadding['pt'] {
    return this.#pt();
  }

  public get pb(): SelectedPadding['pb'] {
    return this.#pb();
  }

  #px: WritableSignal<SelectedPadding['px']> = signal<SelectedPadding['px']>('');
  #py: WritableSignal<SelectedPadding['py']> = signal<SelectedPadding['py']>('');
  #pl: WritableSignal<SelectedPadding['pl']> = signal<SelectedPadding['pl']>('');
  #pr: WritableSignal<SelectedPadding['pr']> = signal<SelectedPadding['pr']>('');
  #pt: WritableSignal<SelectedPadding['pt']> = signal<SelectedPadding['pt']>('');
  #pb: WritableSignal<SelectedPadding['pb']> = signal<SelectedPadding['pb']>('');

  public set<SetPadding extends SelectedPadding>(padding: SetPadding): SetPadding {
    if (padding.px) {
      this.#px.set(`px-${padding.px}`);
    }
    if (padding.py) {
      this.#py.set(`py-${padding.py}`);
    }
    if (padding.pl) {
      this.#pl.set(`pl-${padding.pl}`);
    }
    if (padding.pr) {
      this.#pr.set(`pr-${padding.pr}`);
    }
    if (padding.pt) {
      this.#pt.set(`pt-${padding.pt}`);
    }
    if (padding.pb) {
      this.#pb.set(`pb-${padding.pb}`);
    }

    return {
      px: this.#px(),
      py: this.#py(),
      pl: this.#pl(),
      pr: this.#pr(),
      pt: this.#pt(),
      pb: this.#pb(),
    } as SetPadding;
  }
}

